import Header from'./Header'
import React from 'react';
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function AddRecette()
{
    const [name, setName] = useState("");
    const [file, setFile] = useState("");
    const [description, setDescription] = useState("");
async function AddRecette(){
    console.warn(name,file,description)
    const formData=new FormData();
    formData.append('file',file);
    formData.append('name',name);
    let result=await fetch("http://localhost:8000/api/addrecette",{
    method:'POST',
    body:formData
    });
    alert("Data has beem saved")
}
    const navigate = useNavigate();
    return (
        <div>
        
        <div className="col-sm-6 offset-sm-3">
            <br/>
        <input type="text" className="form-control"
        onChange={(e)=>setName(e.target.value)}
        placeholder="Name"/><br/>
        <input type="file" className="form-control"
        onChange={(e)=>setFile(e.target.files[0])}
        placeholder="file"/><br/>
        <input type="text" className="form-control"
        onChange={(e)=>setDescription(e.target.value)}
        placeholder="description"/><br/>
        <button onClick ={AddRecette} className="btn btn-primary">Add Recette</button>
        </div>
        </div>
        
    );
}
export default AddRecette
